import React, { Component } from 'react';
import { nanoid } from 'nanoid';
import Form from './Form';
import ContactList from './Contacts/ContactList';
import Section from './Section/Section';
import Filter from './Filter/Filter';

export class App extends Component {
  state = {
    contacts: [
      { id: 'id-1', name: 'Rosie Simpson', number: '459-12-56' },
      { id: 'id-2', name: 'Hermione Kline', number: '443-89-12' },
      { id: 'id-3', name: 'Eden Clements', number: '645-17-79' },
      { id: 'id-4', name: 'Annie Copeland', number: '227-91-26' },
    ],
    filter: '',
  };

  formSubmitHendler = contact => {
    const newContact = {
      id: nanoid(),
      name: contact.name,
      number: contact.number,
    };
    console.log(newContact);
    if (
      this.state.contacts.toLowerCase().includes(newContact.name.toLowerCase())
    ) {
      alert('${contact.name} is already in contact');
      return;
    }
    this.setState(({ contacts }) => ({
      todos: [contact, ...contacts],
    }));
  };

  changeFilter = e => {
    this.setState({ filter: e.currentTarget.value });
  };

  getVisibleContacts = () => {
    const { filter, contacts } = this.state;
    const normalizedFilter = filter.toLowerCase();

    return contacts.filter(contact =>
      contact.name.toLowerCase().includes(normalizedFilter)
    );
  };

  deleteCont = id => {
    this.setState(prevState => ({
      contacts: prevState.contacts.filter(contact => id !== contact.id),
    }));
  };
  render() {
    const visibleContacts = this.getVisibleContacts();
    return (
      <div>
        <Section text="Phonebook">
          <Form onSubmit={this.formSubmitHendler} />
        </Section>
        <Section text="Contacts">
          <Filter value={filter} onChange={this.changeFilter} />
        </Section>
        <ContactList elements={visibleContacts} onDelete={this.deleteCont} />
      </div>
    );
  }
}
