import React, { useState } from 'react';
import { nanoid } from 'nanoid';
// import React, { Component } from 'react';
// import PropTypes from 'prop-types';
import s from './Form.module.css';
// import { connect } from 'react-redux';
import { useCreateContactMutation } from 'redux/contactsSlice';

const Form = () => {
  const [name, setName] = useState('');
  const [number, setNumber] = useState('');
  const nameInputId = nanoid();
  const numberInputId = nanoid();

  const [createContact] = useCreateContactMutation;

  const handleChange = e => {
    const { name, value } = e.currentTarget;

    switch (name) {
      case 'name':
        setName(value);
        break;

      case 'number':
        setNumber(value);
        break;

      default:
        console.warn(`There is something wrong`);
    }
  };

  const handleSubmit = e => {
    e.preventDefault();
    createContact(e.currentTarget.elements.value);
    e.currentTarget.reset();
  };

  return (
    <form onSubmit={handleSubmit} className={s.container}>
      <label htmlFor={nameInputId} className={s.label}>
        Name
        <input
          className={s.input}
          type="text"
          name="name"
          pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
          title="Name may contain only letters, apostrophe, dash and spaces. For example Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan"
          required
          value={name}
          onChange={handleChange}
          id={nameInputId}
        />
      </label>
      <br />
      <label htmlFor={numberInputId} className={s.label}>
        Number
        <input
          className={s.input}
          type="tel"
          name="number"
          pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
          title="Phone number must be digits and can contain spaces, dashes, parentheses and can start with +"
          required
          value={number}
          onChange={handleChange}
          id={numberInputId}
        />
      </label>
      <button className={s.btn} type="submit" disabled={isLoading}>
        Add contact
      </button>
    </form>
  );
};

export default Form;
